import React, {useState, useCallback} from "react";
import {useDropzone} from 'react-dropzone'

import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';


const Upload_Resume = () => {
  const [isEnlarged, setIsEnlarged] = useState(false);
  const [currentPage, setCurrentPage] = useState(0);

  const [name, setName] = useState('');

  const onDrop = useCallback(acceptedFiles => {
    // Do something with the files
  }, [])
  const {getRootProps, getInputProps, isDragActive} = useDropzone({onDrop})

  const handleClick = () => {
    setIsEnlarged(!isEnlarged); // Toggle the enlarged state
  };

  const pages = [
    {
      title: 'Page 1',
      content: (
        <div className = 'upload-block' {...getRootProps()}>
          <input {...getInputProps()} />
          {
            isDragActive ?
              <p>Drop the files here ...</p> :
              <p>Drag 'n' drop your resume here, or click to select a file!</p>
          }
        </div>
      ),
    },
    {
      title: 'Page 2',
      content: (
        <div>
          <form>
          <input className="input"
            type="text"
            id="name"
            name="name"
            placeholder="Enter your name here"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          </form>
        </div>
      ),
    },
    // Add more pages as needed
  ];


  const handleNextPage = () => {
      setCurrentPage((prevPage) => Math.min(prevPage + 1, pages.length - 1));
    }



	return (
      <div>

        <button className={`upload-resume-btn ${isEnlarged ? 'hidden-btn' : ''}`} onClick={handleClick}>upload your resume</button>
        <button className = {`hidden-btn-1 ${isEnlarged ? 'next-btn' : ''}`}>next</button>
        <button className = {`hidden-btn-1 ${isEnlarged ? 'back-btn' : ''}`}>back</button>
        <div className={`dynamic-menu ${isEnlarged ? 'dynamic-menu-enlarged' : ''}`}>

        {pages[currentPage].content}

        </div>

      </div>
	);
};

export default Upload_Resume;
