import React, {useState, useCallback} from "react";
import {useDropzone} from 'react-dropzone'
import Papa from 'papaparse';

import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';


const Upload_Resume = () => {
  const [isEnlarged, setIsEnlarged] = useState(false);
  const [currentPage, setCurrentPage] = useState(0);

  const [name, setName] = useState('');
  const [userName, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const [file, setFile] = useState(null);
  const [fileKey, setFileKey] = useState(0);
  const [fileInfo, setFileInfo] = useState({});


  const onDrop = useCallback(acceptedFiles => {
    const dataFile = acceptedFiles[0];
    if (dataFile && (dataFile.type === 'application/msword' || dataFile.type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document')) 
    {
      setFileKey((prevKey) => prevKey + 1);
      setFile(dataFile);
      setFileInfo({'name': dataFile.name, 'size': dataFile.size, 'type': dataFile.type})
      setCurrentPage(1);
    }
    else{
      alert("Error: Invalid Filetype Uploaded. Please submit an MS Word doc.")
    }
  }, [])
  const {getRootProps, getInputProps, isDragActive} = useDropzone({onDrop})

  const handleClick = () => {
    setIsEnlarged(!isEnlarged); // Toggle the enlarged state
  };

  const pages = [
    {
      title: 'Page 1',
      content: (
        <div className = 'upload-block' {...getRootProps()}>
          <input {...getInputProps()} />
          {
            isDragActive ?
              <p>Drop the files here ...</p> :
              <p>Drag 'n' drop your resume here, or click to select a file!</p>
          }
        </div>
      ),
    },
    {
      title: 'Page 2',
      content: (
        <div>
          <form>

          <input className="input"
            type="text"
            id="name"
            name="name"
            placeholder="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          /><br></br>
          <input className="input"
            type="text"
            id="email"
            name="email"
            placeholder="email"
            value={userName}
            onChange={(e) => setUsername(e.target.value)}
          /><br></br>
          <input className="input"
            type="text"
            id="password"
            name="password"
            placeholder="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          </form>
          <button className = {`hidden-btn-1 ${isEnlarged && currentPage == 1? 'next-btn' : ''}`} onClick = {handleNextPage}>create profile</button>
        </div>
      ),
    },
    // Add more pages as needed
  ];


  const handleNextPage = () => {
      setCurrentPage((prevPage) => Math.min(prevPage + 1, pages.length - 1));
    }

  const handlePrevPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 0));
  };



	return (
      <div>

        <button className={`upload-resume-btn ${isEnlarged ? 'hidden-btn' : ''}`} onClick={handleClick}>upload your resume</button>

        <button className = {`hidden-btn-1 ${isEnlarged && currentPage == 1? 'back-btn' : ''}`} onClick = {handlePrevPage}>back</button>
        <div className={`dynamic-menu ${isEnlarged ? 'dynamic-menu-enlarged' : ''}`}>

        {pages[currentPage].content}

        </div>

      </div>
	);
};

export default Upload_Resume;
