import React, {useState, useCallback} from "react";
import {useDropzone} from 'react-dropzone'

import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';


const Upload_Resume = () => {
  const [isEnlarged, setIsEnlarged] = useState(false);

  const onDrop = useCallback(acceptedFiles => {
    // Do something with the files
  }, [])
  const {getRootProps, getInputProps, isDragActive} = useDropzone({onDrop})

  const handleClick = () => {
    setIsEnlarged(!isEnlarged); // Toggle the enlarged state
  };




	return (
      <div>

        <button className={`upload-resume-btn ${isEnlarged ? 'hidden-btn' : ''}`} onClick={handleClick}>upload your resume</button>
        <div className={`dynamic-menu ${isEnlarged ? 'dynamic-menu-enlarged' : ''}`}>

        <div {...getRootProps()}>
          <input {...getInputProps()} />
          {
            isDragActive ?
              <p>Drop the files here ...</p> :
              <p>Drag 'n' drop some files here, or click to select files</p>
          }
        </div>

        </div>

      </div>
	);
};

export default Upload_Resume;
